import axios from 'axios';

// Create an instance of axios with default config
const api = axios.create({
  baseURL: 'https://assignment.reachinbox.com/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add a request interceptor to include auth token
api.interceptors.request.use(
  (config) => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user && user.token) {
      config.headers.Authorization = `Bearer ${user.token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// API functions
export const loginWithGoogle = async () => {
  const response = await api.get('/google-login');
  return response.data;
};

export const getOneboxList = async () => {
  const response = await api.get('/onebox/list');
  return response.data;
};

export const getOneboxThread = async (threadId) => {
  const response = await api.get(`/onebox/${threadId}`);
  return response.data;
};

export const deleteOneboxThread = async (threadId) => {
  const response = await api.delete(`/onebox/${threadId}`);
  return response.data;
};

export const sendReply = async (threadId, replyData) => {
  const response = await api.post(`/reply/${threadId}`, replyData);
  return response.data;
};

export default api;
