import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { fetchOneboxListDirect, sendDirectApiRequest } from '../services/api';

const ApiTesterContainer = styled.div`
  margin: 20px;
  padding: 20px;
  border: 1px solid var(--border-color);
  border-radius: 8px;
  background-color: var(--background-light);
`;

const Title = styled.h2`
  font-size: 18px;
  margin-bottom: 15px;
  color: var(--text-primary);
`;

const EndpointInput = styled.input`
  width: 100%;
  padding: 10px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  margin-bottom: 10px;
  font-family: monospace;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
`;

const Button = styled.button`
  padding: 10px 16px;
  background-color: var(--primary-color);
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-weight: 500;
  
  &:hover {
    opacity: 0.9;
  }
  
  &:disabled {
    background-color: var(--gray-light);
    cursor: not-allowed;
  }
`;

const ResultContainer = styled.div`
  margin-top: 20px;
`;

const ResultTitle = styled.h3`
  font-size: 16px;
  margin-bottom: 10px;
  color: var(--text-primary);
`;

const ResultPreview = styled.pre`
  background-color: #f5f5f5;
  padding: 10px;
  border-radius: 4px;
  overflow: auto;
  max-height: 300px;
  font-family: monospace;
  font-size: 12px;
  color: #333;
`;

const ErrorMessage = styled.div`
  color: #d32f2f;
  margin-top: 10px;
  padding: 10px;
  background-color: rgba(211, 47, 47, 0.1);
  border-radius: 4px;
`;

const StatusBadge = styled.span`
  display: inline-block;
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 12px;
  font-weight: 500;
  margin-left: 10px;
  background-color: ${props => props.success ? 'rgba(76, 175, 80, 0.1)' : 'rgba(211, 47, 47, 0.1)'};
  color: ${props => props.success ? '#4CAF50' : '#d32f2f'};
`;

const OptionsContainer = styled.div`
  margin-bottom: 15px;
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  gap: 16px;
`;

const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  cursor: pointer;
  font-size: 14px;
  color: var(--text-secondary);
`;

const Checkbox = styled.input`
  margin-right: 8px;
`;

const InfoMessage = styled.div`
  color: #1976d2;
  margin-top: 10px;
  padding: 10px;
  background-color: rgba(25, 118, 210, 0.1);
  border-radius: 4px;
  font-size: 14px;
`;

const ApiTester = () => {
  const [endpoint, setEndpoint] = useState('https://hiring.reachinbox.xyz/api/v1/onebox/list');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [useCorsProxy, setUseCorsProxy] = useState(false);

  const handleOneboxListRequest = async () => {
    setLoading(true);
    setError(null);
    setResult(null);
    setSuccess(false);
    
    try {
      const data = await fetchOneboxListDirect(useCorsProxy);
      setResult(data);
      setSuccess(true);
    } catch (err) {
      setError(err.message || 'An error occurred while fetching data');
      setSuccess(false);
    } finally {
      setLoading(false);
    }
  };

  const handleCustomEndpointRequest = async () => {
    if (!endpoint) return;
    
    setLoading(true);
    setError(null);
    setResult(null);
    setSuccess(false);
    
    try {
      const data = await sendDirectApiRequest(endpoint, useCorsProxy);
      setResult(data);
      setSuccess(true);
    } catch (err) {
      setError(err.message || 'An error occurred while fetching data');
      setSuccess(false);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ApiTesterContainer>
      <Title>API Request Tester {success && <StatusBadge success>Success</StatusBadge>} {error && <StatusBadge>Failed</StatusBadge>}</Title>
      
      <EndpointInput
        type="text"
        value={endpoint}
        onChange={(e) => setEndpoint(e.target.value)}
        placeholder="Enter API endpoint"
      />

      <OptionsContainer>
        <CheckboxLabel>
          <Checkbox 
            type="checkbox" 
            checked={useCorsProxy}
            onChange={(e) => setUseCorsProxy(e.target.checked)}
          />
          Use CORS Proxy (for development)
        </CheckboxLabel>
      </OptionsContainer>
      
      {useCorsProxy && (
        <InfoMessage>
          Using a CORS proxy to bypass CORS restrictions. This is only for development/testing purposes.
          Note: The CORS Anywhere demo server may require you to request temporary access at 
          <a href="https://cors-anywhere.herokuapp.com/corsdemo" target="_blank" rel="noopener noreferrer"> https://cors-anywhere.herokuapp.com/corsdemo</a>
        </InfoMessage>
      )}
      
      <ButtonContainer>
        <Button 
          onClick={handleOneboxListRequest} 
          disabled={loading}
        >
          {loading ? 'Loading...' : 'Request Onebox List'}
        </Button>
        
        <Button 
          onClick={handleCustomEndpointRequest} 
          disabled={loading || !endpoint}
        >
          {loading ? 'Loading...' : 'Send Custom Request'}
        </Button>
      </ButtonContainer>
      
      {error && (
        <ErrorMessage>
          Error: {error}
        </ErrorMessage>
      )}
      
      {result && (
        <ResultContainer>
          <ResultTitle>Response:</ResultTitle>
          <ResultPreview>
            {JSON.stringify(result, null, 2)}
          </ResultPreview>
        </ResultContainer>
      )}
    </ApiTesterContainer>
  );
};

export default ApiTester;
