import React, { useState, useContext } from 'react';
import styled from 'styled-components';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { AuthContext } from '../contexts/AuthContext';
import { sendReply } from '../services/api';

const EditorContainer = styled.div`
  margin-top: 24px;
  border: 1px solid var(--border-color);
  border-radius: 8px;
  overflow: hidden;
  background-color: var(--background-light);
`;

const EditorHeader = styled.div`
  padding: 12px 16px;
  border-bottom: 1px solid var(--border-color);
  background-color: var(--gray-light);
`;

const RecipientField = styled.div`
  margin-bottom: 8px;
`;

const Label = styled.span`
  font-weight: 600;
  color: var(--text-primary);
  margin-right: 8px;
`;

const Value = styled.span`
  color: var(--text-secondary);
`;

const SubjectField = styled.div`
  margin-bottom: 8px;
`;

const EditorContent = styled.div`
  .quill {
    background-color: var(--background-light);
    
    .ql-toolbar {
      border-bottom: 1px solid var(--border-color);
      background-color: var(--gray-light);
    }
    
    .ql-container {
      min-height: 200px;
      font-size: 16px;
      
      .ql-editor {
        min-height: 200px;
        color: var(--text-primary);
      }
    }
  }
`;

const EditorFooter = styled.div`
  padding: 12px 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-top: 1px solid var(--border-color);
  background-color: var(--gray-light);
`;

const SendButton = styled.button`
  background-color: var(--primary-color);
  color: white;
  border: none;
  border-radius: 4px;
  padding: 8px 16px;
  font-weight: 500;
  cursor: pointer;
  
  &:hover {
    opacity: 0.9;
  }
`;

const CancelButton = styled.button`
  background-color: transparent;
  color: var(--text-secondary);
  border: 1px solid var(--border-color);
  border-radius: 4px;
  padding: 8px 16px;
  font-weight: 500;
  cursor: pointer;
  margin-right: 12px;
  
  &:hover {
    background-color: var(--gray-light);
  }
`;

const CustomButton = styled.button`
  background-color: ${props => props.primary ? 'var(--success-color)' : 'var(--warning-color)'};
  color: white;
  border: none;
  border-radius: 4px;
  padding: 8px 16px;
  font-weight: 500;
  cursor: pointer;
  margin-right: 12px;
  
  &:hover {
    opacity: 0.9;
  }
`;

const VariablesDropdown = styled.select`
  padding: 8px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  background-color: var(--background-light);
  color: var(--text-primary);
  margin-right: 12px;
`;

const ButtonGroup = styled.div`
  display: flex;
  align-items: center;
`;

const CustomToolbar = styled.div`
  display: flex;
  padding: 8px;
  background-color: var(--gray-light);
  border-bottom: 1px solid var(--border-color);
`;

const modules = {
  toolbar: [
    [{ 'header': [1, 2, 3, false] }],
    ['bold', 'italic', 'underline', 'strike'],
    [{ 'color': [] }, { 'background': [] }],
    [{ 'list': 'ordered' }, { 'list': 'bullet' }],
    ['link', 'image'],
    ['clean']
  ]
};

const formats = [
  'header',
  'bold', 'italic', 'underline', 'strike',
  'color', 'background',
  'list', 'bullet',
  'link', 'image'
];

const ReplyEditor = ({ threadId, to, subject, onCancel }) => {
  const { user } = useContext(AuthContext);
  const [content, setContent] = useState('');
  const [isSending, setIsSending] = useState(false);
  
  const handleSend = async () => {
    if (!content.trim()) return;
    
    setIsSending(true);
    try {
      const replyData = {
        from: user?.user?.email || 'me@example.com',
        to: to,
        subject: subject,
        body: content
      };
      
      await sendReply(threadId, replyData);
      onCancel(); // Close editor after successful send
    } catch (error) {
      console.error('Error sending reply:', error);
      // In a real app, show error message to user
    } finally {
      setIsSending(false);
    }
  };
  
  const handleSave = () => {
    // In a real implementation, this would save the draft
    alert('Draft saved successfully!');
  };
  
  const insertVariable = (e) => {
    const variable = e.target.value;
    if (!variable) return;
    
    // Insert the variable at cursor position
    // For simplicity, we'll just append it to the current content
    setContent(content + ' ' + variable + ' ');
    
    // Reset dropdown
    e.target.value = '';
  };
  
  return (
    <EditorContainer>
      <EditorHeader>
        <RecipientField>
          <Label>To:</Label>
          <Value>{to}</Value>
        </RecipientField>
        <SubjectField>
          <Label>Subject:</Label>
          <Value>{subject}</Value>
        </SubjectField>
      </EditorHeader>
      
      <CustomToolbar>
        <VariablesDropdown onChange={insertVariable} defaultValue="">
          <option value="">Insert Variable</option>
          <option value="{{name}}">Name</option>
          <option value="{{email}}">Email</option>
          <option value="{{company}}">Company</option>
          <option value="{{date}}">Current Date</option>
        </VariablesDropdown>
        <CustomButton primary onClick={handleSave}>SAVE</CustomButton>
      </CustomToolbar>
      
      <EditorContent>
        <ReactQuill
          theme="snow"
          value={content}
          onChange={setContent}
          modules={modules}
          formats={formats}
          placeholder="Write your reply here..."
        />
      </EditorContent>
      
      <EditorFooter>
        <ButtonGroup>
          <CancelButton onClick={onCancel}>Cancel</CancelButton>
        </ButtonGroup>
        <SendButton onClick={handleSend} disabled={isSending}>
          {isSending ? 'Sending...' : 'Send'}
        </SendButton>
      </EditorFooter>
    </EditorContainer>
  );
};

export default ReplyEditor;
